    "css": "slice-portfolio {\r\n  display: block;\r\n  min-height: 100vh;\r\n  font-family: var(--font-family);\r\n  color: var(--font-primary-color);\r\n  background-color: var(--primary-background-color);\r\n}\r\n\r\n.portfolio-container {\r\n  width: 100%;\r\n  min-height: 100vh;\r\n  display: flex;\r\n  flex-direction: column;\r\n}\r\n\r\n.portfolio-main {\r\n  flex: 1;\r\n  padding-top: 40px; /* Account for fixed navbar */\r\n  min-height: calc(100vh - 120px);\r\n}\r\n\r\n@media (max-width: 768px) {\r\n  .portfolio-main {\r\n    padding-top: 58px; /* Account for fixed navbar */\r\n    min-height: calc(100vh - 120px);\r\n  }\r\n}\r\n\r\n\r\n.portfolio-content.smooth-transitions {\r\n  transition: opacity 0.3s ease-in-out, transform 0.3s ease-in-out;\r\n}\r\n\r\n.content-transition-out {\r\n  opacity: 0.7;\r\n  transform: translateY(10px);\r\n}\r\n\r\n.content-transition-in {\r\n  opacity: 1;\r\n  transform: translateY(0);\r\n}\r\n\r\n\r\n.portfolio-content {\r\n  width: 100%;\r\n  max-width: 1200px;\r\n  margin: 0 auto;\r\n  padding: 2rem;\r\n  opacity: 1;\r\n  transition: opacity 0.3s ease-in-out;\r\n}\r\n\r\n.content-transition {\r\n  opacity: 0.7;\r\n}\r\n\r\n/* Footer Styles */\r\n.portfolio-footer {\r\n  background-color: var(--secondary-background-color);\r\n  padding: 3rem 2rem 2rem;\r\n  margin-top: auto;\r\n  border-top: 1px solid var(--primary-color-shade);\r\n}\r\n\r\n.social-links {\r\n  display: flex;\r\n  justify-content: center;\r\n  gap: 1.5rem;\r\n  margin-bottom: 2rem;\r\n  flex-wrap: wrap;\r\n}\r\n\r\n.social-link {\r\n  display: inline-flex;\r\n  align-items: center;\r\n  gap: 0.75rem;\r\n  padding: 0.75rem 1.5rem;\r\n  background-color: var(--card-background-color);\r\n  color: var(--font-primary-color);\r\n  text-decoration: none;\r\n  border-radius: 50px;\r\n  transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);\r\n  font-weight: 600;\r\n  box-shadow: 0 4px 6px rgba(0,0,0,0.1);\r\n  border: 2px solid transparent;\r\n}\r\n\r\n.social-icon {\r\n  display: flex;\r\n  align-items: center;\r\n  justify-content: center;\r\n  width: 24px;\r\n  height: 24px;\r\n}\r\n\r\n.social-link:hover {\r\n  transform: translateY(-4px) scale(1.05);\r\n  filter: brightness(1.1);\r\n}\r\n\r\n/* GitHub - Slate/Black */\r\n.social-link-github {\r\n  background-color: #24292e;\r\n  color: white;\r\n}\r\n.social-link-github:hover {\r\n  box-shadow: 0 10px 20px rgba(36, 41, 46, 0.4);\r\n}\r\n\r\n/* LinkedIn - Blue */\r\n.social-link-linkedin {\r\n  background-color: #0077b5;\r\n  color: white;\r\n}\r\n.social-link-linkedin:hover {\r\n  box-shadow: 0 10px 20px rgba(0, 119, 181, 0.4);\r\n}\r\n\r\n/* Email - Red */\r\n.social-link-email {\r\n  background-color: #ea4335;\r\n  color: white;\r\n}\r\n.social-link-email:hover {\r\n  box-shadow: 0 10px 20px rgba(234, 67, 53, 0.4);\r\n}\r\n\r\n.footer-info {\r\n  text-align: center;\r\n  color: var(--font-secondary-color);\r\n}\r\n\r\n.footer-info p {\r\n  margin: 0.5rem 0;\r\n  font-size: 0.9rem;\r\n}\r\n\r\n.footer-info a {\r\n  color: var(--primary-color);\r\n  text-decoration: none;\r\n  font-weight: 600;\r\n}\r\n\r\n.footer-info a:hover {\r\n  text-decoration: underline;\r\n}\r\n\r\n/* Responsive Design */\r\n@media (max-width: 768px) {\r\n  .portfolio-content {\r\n    padding: 1rem;\r\n  }\r\n  \r\n  .social-links {\r\n    flex-direction: column;\r\n    align-items: center;\r\n    gap: 1rem;\r\n  }\r\n  \r\n  .social-link {\r\n    width: 200px;\r\n    justify-content: center;\r\n  }\r\n}\r\n\r\n/* Animation for content changes */\r\n@keyframes fadeIn {\r\n  from {\r\n    opacity: 0;\r\n    transform: translateY(20px);\r\n  }\r\n  to {\r\n    opacity: 1;\r\n    transform: translateY(0);\r\n  }\r\n}\r\n\r\n.portfolio-content > * {\r\n  animation: fadeIn 0.6s ease-out;\r\n}\r\n\r\n",
