    "css": ".certificate-card-container {\r\n   width: 100%;\r\n}\r\n\r\n/* Certificate Cards - Ultra Compact Vertical Layout */\r\n.certificate-card {\r\n   background-color: var(--secondary-background-color);\r\n   border-radius: var(--border-radius-slice);\r\n   box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);\r\n   overflow: hidden;\r\n   transition: all 0.3s ease;\r\n   animation: fadeInUp 0.6s ease-out forwards;\r\n   opacity: 0;\r\n   transform: translateY(30px);\r\n   border: 1px solid var(--primary-color-shade);\r\n   display: flex;\r\n   flex-direction: column;\r\n   height: 100%;\r\n   margin-bottom: 1rem; /* Ultra reduced from 1.5rem */\r\n}\r\n\r\n.certificate-card:hover {\r\n   transform: translateY(-5px);\r\n   box-shadow: 0 8px 24px rgba(0, 0, 0, 0.15);\r\n}\r\n\r\n/* Certificate Image - Super Compact */\r\n.certificate-image-container {\r\n   position: relative;\r\n   height: 120px; /* Increased to show more of the certificate */\r\n   overflow: hidden;\r\n   background: linear-gradient(135deg, var(--primary-color) 0%, var(--secondary-color) 100%);\r\n   display: flex;\r\n   align-items: center;\r\n   justify-content: center;\r\n}\r\n\r\n.certificate-image {\r\n   width: 100%;\r\n   height: 100%;\r\n   object-fit: cover;\r\n   transition: transform 0.3s ease;\r\n   filter: brightness(0.8) drop-shadow(0 4px 8px rgba(0, 0, 0, 0.3));\r\n}\r\n\r\n.certificate-card:hover .certificate-image {\r\n   transform: scale(1.05);\r\n}\r\n\r\n.issuer-logo {\r\n   position: absolute;\r\n   bottom: 6px;\r\n   right: 6px;\r\n   width: 24px; /* Ultra reduced from 32px */\r\n   height: 24px;\r\n   object-fit: contain;\r\n   background-color: white;\r\n   border-radius: 50%;\r\n   padding: 3px;\r\n   box-shadow: 0 1px 4px rgba(0, 0, 0, 0.2);\r\n}\r\n\r\n/* Certificate Content - Super Compact */\r\n.certificate-content {\r\n   padding: 0.4rem; /* Super reduced from 0.6rem */\r\n   flex: 1;\r\n   display: flex;\r\n   flex-direction: column;\r\n}\r\n\r\n.certificate-header {\r\n   margin-bottom: 0.3rem; /* Super reduced from 0.5rem */\r\n}\r\n\r\n.certificate-title {\r\n   font-size: 0.8rem; /* Super reduced from 0.9rem */\r\n   color: var(--primary-color);\r\n   margin: 0 0 0.1rem 0; /* Super reduced from 0.2rem */\r\n   font-weight: 600;\r\n   line-height: 1.1;\r\n}\r\n\r\n.certificate-issuer {\r\n   font-size: 0.7rem; /* Super reduced from 0.8rem */\r\n   color: var(--font-secondary-color);\r\n   margin: 0 0 0.3rem 0; /* Super reduced from 0.5rem */\r\n   font-weight: 500;\r\n}\r\n\r\n.certificate-description {\r\n   color: var(--font-primary-color);\r\n   line-height: 1.2; /* Super reduced from 1.3 */\r\n   margin-bottom: 0.4rem; /* Super reduced from 0.6rem */\r\n   text-align: justify;\r\n   flex: 1;\r\n   font-size: 0.65rem; /* Super reduced from 0.75rem */\r\n}\r\n\r\n.certificate-meta {\r\n   margin-bottom: 0.4rem; /* Super reduced from 0.6rem */\r\n   padding: 0.3rem; /* Super reduced from 0.5rem */\r\n   background-color: var(--tertiary-background-color);\r\n   border-radius: var(--border-radius-slice);\r\n}\r\n\r\n.certificate-dates {\r\n   font-size: 0.6rem; /* Super reduced from 0.7rem */\r\n   color: var(--font-primary-color);\r\n   margin-bottom: 0.1rem; /* Super reduced from 0.2rem */\r\n   font-weight: 500;\r\n}\r\n\r\n.credential-id {\r\n   font-size: 0.55rem; /* Super reduced from 0.65rem */\r\n   color: var(--font-secondary-color);\r\n   font-family: monospace;\r\n}\r\n\r\n/* Certificate Skills - Super Compact */\r\n.certificate-skills {\r\n   margin-bottom: 0.4rem; /* Super reduced from 0.6rem */\r\n}\r\n\r\n.certificate-skills h5 {\r\n   font-size: 0.65rem; /* Super reduced from 0.75rem */\r\n   color: var(--primary-color);\r\n   margin-bottom: 0.2rem; /* Super reduced from 0.3rem */\r\n   font-weight: 600;\r\n}\r\n\r\n.skills-list {\r\n   display: flex;\r\n   flex-wrap: wrap;\r\n   gap: 0.15rem; /* Super reduced from 0.25rem */\r\n}\r\n\r\n.skill-tag {\r\n   background-color: var(--primary-color);\r\n   color: var(--primary-color-contrast);\r\n   padding: 0.1rem 0.25rem; /* Super reduced from 0.15rem 0.3rem */\r\n   border-radius: 8px;\r\n   font-size: 0.55rem; /* Super reduced from 0.65rem */\r\n   font-weight: 500;\r\n   transition: all 0.3s ease;\r\n}\r\n\r\n.skill-tag:hover {\r\n   background-color: var(--secondary-color);\r\n   color: var(--secondary-color-contrast);\r\n   transform: scale(1.05);\r\n}\r\n\r\n/* Certificate Actions */\r\n.certificate-actions {\r\n   margin-top: auto;\r\n}\r\n\r\n.view-certificate-btn {\r\n   width: 100%;\r\n}\r\n\r\n/* Animations */\r\n@keyframes fadeInUp {\r\n   to {\r\n      opacity: 1;\r\n      transform: translateY(0);\r\n   }\r\n}\r\n\r\n/* Responsive Design - Super Compact */\r\n@media (max-width: 768px) {\r\n   .certificate-content {\r\n      padding: 0.3rem; /* Super reduced from 0.5rem */\r\n   }\r\n   \r\n   .certificate-image-container {\r\n      height: 100px; /* Increased to show more of the certificate */\r\n   }\r\n}\r\n\r\n@media (max-width: 480px) {\r\n   .certificate-content {\r\n      padding: 0.25rem; /* Super reduced from 0.4rem */\r\n   }\r\n   \r\n   .certificate-title {\r\n      font-size: 0.75rem; /* Super reduced from 0.85rem */\r\n   }\r\n   \r\n   .certificate-issuer {\r\n      font-size: 0.65rem; /* Super reduced from 0.75rem */\r\n   }\r\n   \r\n   .certificate-image-container {\r\n      height: 80px; /* Increased to show more of the certificate */\r\n   }\r\n}",
