    "html": "<div class=\"random-facts-container\">\r\n    <div class=\"header-container\">\r\n        <h2 class=\"section-title\">Random Facts</h2>\r\n    </div>\r\n    \r\n    <div class=\"board-wrapper\">\r\n        <div class=\"board\" id=\"corkboard\">\r\n            <div id=\"add-note-btn\" class=\"control-btn add-note-btn\" title=\"Create your own note\">\r\n                <span class=\"icon\">+</span>\r\n            </div>\r\n            <div id=\"trash-can\" class=\"control-zone trash-zone\" title=\"Drag notes here to remove\">\r\n                <span class=\"icon\">ðŸ—‘ï¸</span>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>",
    "css": ".random-facts-container {\r\n    width: 100%;\r\n    margin: 4rem auto;\r\n    padding: 0;\r\n    display: flex;\r\n    flex-direction: column;\r\n    align-items: center;\r\n}\r\n\r\n.header-container {\r\n    width: 100%;\r\n    display: flex;\r\n    flex-direction: column;\r\n    align-items: center;\r\n    margin-bottom: 2rem;\r\n    gap: 0; /* Reduced gap */\r\n}\r\n\r\n.section-title {\r\n    /* Inherit global section-title styles if available, or define locally as fallback */\r\n    font-size: 2.5rem;\r\n    color: var(--primary-color); \r\n    text-align: center;\r\n    margin: 0;\r\n}\r\n\r\n.board-wrapper {\r\n    width: 100%;\r\n    padding: 1rem;\r\n    background: var(--primary-background-color); \r\n    border-radius: 12px;\r\n    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);\r\n}\r\n\r\n.board {\r\n    position: relative;\r\n    width: 100%;\r\n    height: 600px;\r\n    /* Clean thematic background instead of cork */\r\n    background-color: var(--tertiary-background-color); \r\n    border: 2px dashed var(--primary-color-shade); \r\n    border-radius: 8px;\r\n    overflow: hidden;\r\n    touch-action: none;\r\n    transition: background-color 0.3s ease;\r\n}\r\n\r\n.sticky-note {\r\n    position: absolute;\r\n    width: 200px;\r\n    min-height: 200px;\r\n    padding: 1.5rem;\r\n    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05); /* Softer shadow */\r\n    cursor: grab;\r\n    transition: transform 0.2s cubic-bezier(0.34, 1.56, 0.64, 1), box-shadow 0.2s ease, opacity 0.3s ease;\r\n    display: flex;\r\n    justify-content: center;\r\n    align-items: center;\r\n    text-align: center;\r\n    font-family: inherit; /* Use site font */\r\n    font-size: 1.1rem;\r\n    font-weight: 500;\r\n    color: var(--primary-color-contrast); /* White text usually */\r\n    user-select: none;\r\n    z-index: 1;\r\n    border-radius: 0.5rem; /* Rounded corners for modern look */\r\n}\r\n\r\n/* Visual Enhancement: Adhesive Strip */\r\n.sticky-note::before {\r\n    content: '';\r\n    position: absolute;\r\n    top: 0;\r\n    left: 0;\r\n    width: 100%;\r\n    height: 15%; /* Takes top 15% */\r\n    background: linear-gradient(to bottom, rgba(255,255,255,0.4) 0%, rgba(255,255,255,0.1) 80%, rgba(255,255,255,0) 100%);\r\n    border-top-left-radius: 0.5rem;\r\n    border-top-right-radius: 0.5rem;\r\n    z-index: 0;\r\n    pointer-events: none;\r\n}\r\n\r\n.sticky-note.trash-hover {\r\n    transform: scale(0.3) !important;\r\n    opacity: 0.7;\r\n    transition: transform 0.2s ease, opacity 0.2s ease;\r\n}\r\n\r\n/* Specific colors for categories */\r\n.sticky-note.type-tech {\r\n    background-color: var(--primary-color);\r\n}\r\n\r\n.sticky-note.type-fun {\r\n    background-color: var(--secondary-color);\r\n}\r\n\r\n.sticky-note.type-personal {\r\n    background-color: var(--success-color);\r\n}\r\n\r\n.sticky-note:active {\r\n    cursor: grabbing;\r\n    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);\r\n    z-index: 1000 !important;\r\n}\r\n\r\n/* Pin becomes a functional interactive element */\r\n/* (Previous styles remain, adding new ones) */\r\n\r\n.control-btn {\r\n    position: absolute;\r\n    width: 50px;\r\n    height: 50px;\r\n    background: white;\r\n    border-radius: 50%;\r\n    display: flex;\r\n    justify-content: center;\r\n    align-items: center;\r\n    box-shadow: 0 4px 6px rgba(0,0,0,0.1);\r\n    cursor: pointer;\r\n    z-index: 2000;\r\n    transition: transform 0.2s, background-color 0.2s;\r\n    font-size: 1.5rem;\r\n    color: var(--primary-color);\r\n}\r\n\r\n.add-note-btn {\r\n    top: 20px;\r\n    right: 20px;\r\n}\r\n\r\n.add-note-btn:hover {\r\n    transform: scale(1.1);\r\n    background: #f0f0f0;\r\n}\r\n\r\n.trash-zone {\r\n    position: absolute;\r\n    bottom: 20px;\r\n    right: 20px;\r\n    width: 60px;\r\n    height: 60px;\r\n    border-radius: 50%;\r\n    display: flex;\r\n    justify-content: center;\r\n    align-items: center;\r\n    font-size: 2rem;\r\n    opacity: 0.5;\r\n    transition: all 0.3s ease;\r\n    z-index: 10;\r\n    border: 2px dashed transparent;\r\n}\r\n\r\n.trash-zone.drag-over {\r\n    opacity: 1;\r\n    transform: scale(1.2);\r\n    background-color: rgba(255, 0, 0, 0.1);\r\n    border-color: rgba(255, 0, 0, 0.3);\r\n}\r\n\r\n.sticky-note.user-note {\r\n    background-color: #fef08a !important; /* Yellow-ish */\r\n    color: #333;\r\n    font-family: 'Courier New', Courier, monospace; /* Handwriting feel */\r\n}\r\n\r\n.edit-note-btn {\r\n    position: absolute;\r\n    top: 8px;\r\n    right: 8px;\r\n    width: 24px;\r\n    height: 24px;\r\n    cursor: pointer;\r\n    opacity: 0.4;\r\n    transition: opacity 0.2s, transform 0.2s;\r\n    z-index: 10;\r\n    color: #444;\r\n    display: flex;\r\n    align-items: center;\r\n    justify-content: center;\r\n    background: rgba(255, 255, 255, 0.4);\r\n    border-radius: 4px;\r\n}\r\n\r\n.edit-note-btn:hover {\r\n    opacity: 1;\r\n    transform: scale(1.1);\r\n    background: rgba(255, 255, 255, 0.8);\r\n}\r\n\r\n/* User Editable Area */\r\n.sticky-note .editable-content {\r\n    width: 100%;\r\n    height: 100%;\r\n    outline: none;\r\n    cursor: text;\r\n    pointer-events: auto; /* allow text selection */\r\n    user-select: text; /* Enable selection explicitly */\r\n    -webkit-user-select: text;\r\n}\r\n\r\n.sticky-note.dragging .editable-content {\r\n    pointer-events: none; /* disable text selection while dragging */\r\n}\r\n\r\n@media (max-width: 768px) {\r\n    .board {\r\n        height: 500px;\r\n    }\r\n    .sticky-note {\r\n        width: 150px;\r\n        min-height: 150px;\r\n        font-size: 0.9rem;\r\n        padding: 1rem;\r\n    }\r\n}",
