    "html": "<div class=\"slice-timeline-wrapper\">\r\n  <svg class=\"timeline-svg\" width=\"100%\" height=\"100%\" style=\"position: absolute; top:0; left:0; z-index:0; pointer-events:none;\"></svg>\r\n  <ul class=\"timeline-list\"></ul>\r\n</div>",
    "css": "/* Styles for Timeline component */\r\n\r\n.slice-timeline-wrapper {\r\n    /* Reduced top padding since header handles spacing, kept bottom for graph droops */\r\n    padding: 1rem 1rem 8rem 1rem;\r\n    width: 100%;\r\n    display: flex;\r\n    flex-direction: column;\r\n    align-items: center;\r\n    position: relative;\r\n    box-sizing: border-box;\r\n}\r\n\r\n.timeline-svg {\r\n    position: absolute;\r\n    top: 0;\r\n    left: 0;\r\n    width: 100%;\r\n    height: 100%;\r\n    z-index: 0;\r\n    pointer-events: none;\r\n}\r\n\r\n.timeline-list {\r\n    list-style: none;\r\n    padding: 0;\r\n    margin: 0;\r\n    position: relative;\r\n    display: flex;\r\n    flex-direction: row;\r\n    justify-content: space-between;\r\n    width: 100%;\r\n    max-width: 1000px;\r\n    z-index: 1; /* Above SVG */\r\n}\r\n\r\n.slice-timeline-wrapper.horizontal .timeline-list {\r\n    flex-direction: row;\r\n    /* justify-content handled mostly by flex spacing */\r\n}\r\n\r\n.slice-timeline-wrapper.vertical {\r\n    align-items: flex-start; /* Or center? */\r\n}\r\n\r\n.slice-timeline-wrapper.vertical .timeline-list {\r\n    flex-direction: column;\r\n    /* Need height? Or just flow */\r\n    height: auto;\r\n    width: auto;\r\n    min-height: 500px; /* Arbitrary, maybe 100% */\r\n}\r\n\r\n/* Base item styles */\r\n.timeline-item {\r\n    position: relative;\r\n    z-index: 1;\r\n    display: flex;\r\n    flex-direction: column;\r\n    align-items: center;\r\n    cursor: grab; /* Show grab cursor */\r\n    flex: 1;\r\n    opacity: 0.7;\r\n    transition: transform 0.4s ease, opacity 0.4s ease; /* Animate transform and opacity */\r\n    min-width: 80px;\r\n    user-select: none; /* Prevent text params during drag */\r\n}\r\n\r\n.timeline-item.dragging {\r\n    cursor: grabbing;\r\n    z-index: 100; /* Top most when dragging */\r\n    opacity: 1;\r\n    /* transition: none; handled in JS for transform */\r\n}\r\n\r\n/* Orientation Specific Layouts - Transforms are handled inline now */\r\n.slice-timeline-wrapper.horizontal .timeline-item {\r\n    /* transform handled by JS */\r\n}\r\n\r\n.slice-timeline-wrapper.vertical .timeline-item {\r\n    /* transform handled by JS */\r\n    flex-direction: row; /* Align content horizontally */\r\n    margin: 2rem 0;\r\n    width: 200px; /* Or variable */\r\n}\r\n\r\n/* Vertical Timeline Item Internal Layout */\r\n.slice-timeline-wrapper.vertical .timeline-dot {\r\n    order: 0;\r\n    margin: 0 1rem;\r\n}\r\n.slice-timeline-wrapper.vertical .timeline-date {\r\n    order: -1;\r\n    text-align: right;\r\n    min-width: 60px;\r\n}\r\n.slice-timeline-wrapper.vertical .timeline-label {\r\n    order: 1;\r\n    text-align: left;\r\n}\r\n\r\n.timeline-item:hover {\r\n    opacity: 1;\r\n}\r\n\r\n.timeline-item.active {\r\n    opacity: 1;\r\n    z-index: 2; /* Bring active to front if overlaps */\r\n}\r\n\r\n/* Date / Top Label */\r\n.timeline-date {\r\n    font-size: 0.9rem;\r\n    font-weight: 700;\r\n    color: var(--font-secondary-color);\r\n    margin-bottom: 1rem;\r\n    transition: all 0.3s;\r\n    opacity: 0.8;\r\n}\r\n\r\n.timeline-item.active .timeline-date {\r\n    color: var(--primary-color);\r\n    opacity: 1;\r\n}\r\n\r\n/* The Node Dot */\r\n.timeline-dot {\r\n    width: 18px;\r\n    height: 18px;\r\n    background: var(--background-color);\r\n    border: 3px solid var(--primary-color); /* Primary color border */\r\n    border-radius: 50%;\r\n    transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);\r\n    position: relative;\r\n    z-index: 2;\r\n    box-shadow: 0 0 0 4px var(--background-color); /* Separation from line */\r\n}\r\n\r\n/* Active Node Style */\r\n.timeline-item.active .timeline-dot {\r\n    background: var(--primary-color);\r\n    transform: scale(1.3);\r\n    box-shadow: 0 0 0 6px var(--background-color);\r\n}\r\n\r\n.timeline-label {\r\n    margin-top: 1rem;\r\n    font-size: 0.85rem;\r\n    text-align: center;\r\n    font-weight: 500;\r\n    color: var(--font-primary-color);\r\n    max-width: 120px;\r\n    transition: all 0.3s;\r\n    opacity: 0.9;\r\n}\r\n\r\n.timeline-item.active .timeline-label {\r\n    font-weight: 700;\r\n}\r\n\r\n/* Mobile / Vertical Variant */\r\n@media (max-width: 768px) {\r\n    .slice-timeline-wrapper.horizontal {\r\n        align-items: flex-start;\r\n        padding-left: 1rem;\r\n        padding-right: 1rem;\r\n    }\r\n    \r\n    /* Hide the complex graph SVG on mobile only if horizontal? \r\n       Or maybe users want the graph on mobile too? \r\n       Previous req said hide it. Let's keep it hidden for simplicity */\r\n    .slice-timeline-wrapper.horizontal .timeline-svg {\r\n        display: none;\r\n    }\r\n    \r\n    .slice-timeline-wrapper.horizontal .timeline-list {\r\n        flex-direction: column;\r\n        width: 100%;\r\n        gap: 0;\r\n        margin-left: 0;\r\n        padding-left: 0;\r\n    }\r\n    \r\n    /* Mobile Track Line */\r\n    .slice-timeline-wrapper.horizontal .timeline-list::before {\r\n        content: '';\r\n        position: absolute;\r\n        top: 10px;\r\n        bottom: 10px;\r\n        left: 20px; \r\n        width: 3px;\r\n        background: var(--primary-color);\r\n        opacity: 0.3;\r\n        z-index: 0;\r\n    }\r\n\r\n    .slice-timeline-wrapper.horizontal .timeline-item {\r\n        display: grid;\r\n        grid-template-columns: 40px 1fr; \r\n        grid-template-rows: auto auto;\r\n        column-gap: 0;\r\n        row-gap: 0.2rem;\r\n        \r\n        width: 100%;\r\n        margin-bottom: 2rem;\r\n        flex-direction: row; \r\n        align-items: start;\r\n        text-align: left;\r\n        \r\n        /* Disable ZigZag/Drag Transform on Mobile */\r\n        transform: none !important;\r\n        opacity: 1; \r\n        cursor: pointer; /* No grab on mobile usually */\r\n    }\r\n    \r\n    .timeline-dot {\r\n        grid-row: 1 / span 2;\r\n        grid-column: 1;\r\n        justify-self: center;\r\n        margin-top: 5px;\r\n        /* Center on the track line (20px) */\r\n    }\r\n    \r\n    .timeline-date {\r\n        grid-row: 1;\r\n        grid-column: 2;\r\n        margin-bottom: 0;\r\n        text-align: left;\r\n    }\r\n    \r\n    .timeline-label {\r\n        grid-row: 2;\r\n        grid-column: 2;\r\n        text-align: left;\r\n        max-width: 100%;\r\n        margin-top: 0.2rem;\r\n    }\r\n}\r\n\r\n",
